components.custom:
var inn = data.idocs_inn;

if (inn) {
    fetch('/micro/integrations/records/query',{
        method: 'POST',
        credentials: 'include',
        headers: {'Content-type': 'application/json;charset=UTF-8'},
        body: JSON.stringify({
            "query": {
                "sourceId": "dadata-party",
                "query":{"tin": inn},
                "attributes": {"subjectName": "subjectName"},
                "main": true
            }})
    }).then(response => { return response.json();}).then(function (response){

      response = (response.suggestions || [])[0];

      var attributes = [
          'idocs_fullOrganizationName',
          'idocs_shortOrganizationName',
          'idocs_juridicalAddress',
          'idocs_kpp',
          'idocs_ogrn',
          'idocs_okpo',
          'idocs_CEOname',
          'idocs_postAddress'
      ];
      var map = {
          'idocs_fullOrganizationName': function(){return response.value},
          'idocs_shortOrganizationName': function(){return response.unrestricted_value},
          'idocs_juridicalAddress': function(){return response.data.address.unrestricted_value},
          'idocs_kpp': function(){return response.data.kpp},
          'idocs_ogrn': function(){return response.data.ogrn},
          'idocs_okpo': function(){return response.data.okpo},
          'idocs_CEOname': function(){return response.data.management.post + ', ' + response.data.management.name},
          'idocs_postAddress': function(){return response.data.address.unrestricted_value},
      };
      var needAsk = false;

      if (response) {
          for (var i=0, count=attributes.length; i < count; i++) {
              var attribute = attributes[i];
              var formAttributeValue = data[attribute];

              if (formAttributeValue && formAttributeValue !== map[attribute]()) {
                  needAsk = true;
                  break;
              }
          }

          var rewrite = () => {
              utils.eachComponent(form.components, component => {
                  var newValue = map[component.key] && map[component.key]();
                  newValue && component.setValue(newValue);
              });
          };

          needAsk ? Citeck.forms.confirm('Перезаписать заполненные поля формы?', rewrite):rewrite();
      }
    });
}

